# Napisać program, który wyznacza n-tą cyfrę po przecinku rozwinięcia dziesiętnego wartości
# sqrt(2). Program powinien działać poprawnie dla n < 100.


def is_square_bigger_than_two(tab)->bool:
    n = len(tab)
    result = [0 for _ in range(2*n)]
    for i in range(n-1, -1, -1): # n-1, n-2, ..., 3, 2, 1, 0
        for j in range(n-1, -1, -1):
            result[i+j] += tab[i] * tab[j]

    for i in range(2*n - 2, 0, -1):
        if(result[i] > 9):
            result[i-1] += result[i] // 10
            result[i] = result[i] % 10

    if result[0] > 1:
        return True
    return False

n = 121
t = [0 for _ in range(n+1)]

t[0] = 1
current_digit = 1
while current_digit <= n:
    for i in range(1, 10):
        t[current_digit] = i
        if is_square_bigger_than_two(t):
            t[current_digit] = i-1
            break

    current_digit += 1

# wolfram
sqrt_2=""
print("Result:", t[n])
print("Wolfram:", sqrt_2[n], "max:", len(sqrt_2))